package com.food.spider.model;import com.alibaba.fastjson.JSON;import java.util.ArrayList;/** * Created by user on 16-12-3. */public class Shop {    String name;    String address;    ArrayList<String> tels;    Point point;    String shopHours;    int consumptionPerPerson;    float flavor = 0.0f;    float environmentalScience = 0.0f;    float service = 0.0f;    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getAddress() {        return address;    }    public void setAddress(String address) {        this.address = address;    }    public ArrayList<String> getTels() {        return tels;    }    public void setTels(ArrayList<String> tels) {        this.tels = tels;    }    public Point getPoint() {        return point;    }    public void setPoint(Point point) {        this.point = point;    }    public String getShopHours() {        return shopHours;    }    public void setShopHours(String shopHours) {        this.shopHours = shopHours;    }    public int getConsumptionPerPerson() {        return consumptionPerPerson;    }    public void setConsumptionPerPerson(int consumptionPerPerson) {        this.consumptionPerPerson = consumptionPerPerson;    }    public float getFlavor() {        return flavor;    }    public void setFlavor(float flavor) {        this.flavor = flavor;    }    public float getEnvironmentalScience() {        return environmentalScience;    }    public void setEnvironmentalScience(float environmentalScience) {        this.environmentalScience = environmentalScience;    }    public float getService() {        return service;    }    public void setService(float service) {        this.service = service;    }    /**     * 静态工厂 生成对象类     *     * @return     */    public static Shop getNewStance(String name, String address, ArrayList<String> tels            , Point point, String shopHours, int consumptionPerPerson, float flavor            , float environmentalScience, float service) {        Shop shop = new Shop();        shop.setName(name);        shop.setAddress(address);        shop.setTels(tels);        shop.setPoint(point);        shop.setShopHours(shopHours);        shop.setConsumptionPerPerson(consumptionPerPerson);        shop.setFlavor(flavor);        shop.setEnvironmentalScience(environmentalScience);        shop.setService(service);        return shop;    }    @Override    public String toString() {        return JSON.toJSONString(this);    }}